import React, { useState } from "react";
import { useHistory } from "react-router-dom";

const emailValidator = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
const passwordValidator = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,}$/;

const FormComponent = () => {
  const [formState, setFormState] = useState({
    firstName: "",
    lastName: "",
    username: "",
    email: "",
    password: "",
    showPassword: false,
    phoneNo: "",
    country: "",
    city: "",
    panNo: "",
    aadharNo: "",
    errors: {}
  });

  const history = useHistory();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormState({
      ...formState,
      [name]: value
    });
  };

  const validate = () => {
    const errors = {};

    if (!formState.firstName.trim()) errors.firstName = "First Name is required";
    if (!formState.lastName.trim()) errors.lastName = "Last Name is required";
    if (!formState.username.trim()) errors.username = "Username is required";
    if (!formState.email.trim() || !emailValidator.test(formState.email))
      errors.email = "Valid Email is required";
    if (!formState.password.trim() || !passwordValidator.test(formState.password))
      errors.password = "Password must contain at least 8 characters, 1 number, 1 uppercase, and 1 lowercase";
    if (!formState.phoneNo.trim()) errors.phoneNo = "Phone Number is required";
    if (!formState.country.trim()) errors.country = "Country is required";
    if (!formState.city.trim()) errors.city = "City is required";
    if (!formState.panNo.trim()) errors.panNo = "Pan No. is required";
    if (!formState.aadharNo.trim()) errors.aadharNo = "Aadhar No. is required";

    setFormState({ ...formState, errors });
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validate()) {
      history.push("/details", { ...formState });
    }
  };

  const togglePasswordVisibility = () => {
    setFormState({
      ...formState,
      showPassword: !formState.showPassword
    });
  };

  return (
    <div>
      <h3>Sign Up Form</h3>
      <form onSubmit={handleSubmit}>
        <div>
          <input
            type="text"
            name="firstName"
            placeholder="First Name"
            value={formState.firstName}
            onChange={handleChange}
          />
          {formState.errors.firstName && <div className="error">{formState.errors.firstName}</div>}
        </div>
        <div>
          <input
            type="text"
            name="lastName"
            placeholder="Last Name"
            value={formState.lastName}
            onChange={handleChange}
          />
          {formState.errors.lastName && <div className="error">{formState.errors.lastName}</div>}
        </div>
        <div>
          <input
            type="text"
            name="username"
            placeholder="Username"
            value={formState.username}
            onChange={handleChange}
          />
          {formState.errors.username && <div className="error">{formState.errors.username}</div>}
        </div>
        <div>
          <input
            type="email"
            name="email"
            placeholder="E-mail"
            value={formState.email}
            onChange={handleChange}
          />
          {formState.errors.email && <div className="error">{formState.errors.email}</div>}
        </div>
        <div>
          <input
            type={formState.showPassword ? "text" : "password"}
            name="password"
            placeholder="Password"
            value={formState.password}
            onChange={handleChange}
          />
          <button type="button" onClick={togglePasswordVisibility}>
            {formState.showPassword ? "Hide" : "Show"}
          </button>
          {formState.errors.password && <div className="error">{formState.errors.password}</div>}
        </div>
        <div>
          <input
            type="text"
            name="phoneNo"
            placeholder="Phone Number (country code - number)"
            value={formState.phoneNo}
            onChange={handleChange}
          />
          {formState.errors.phoneNo && <div className="error">{formState.errors.phoneNo}</div>}
        </div>
        <div>
          <select name="country" value={formState.country} onChange={handleChange}>
            <option value="">Select Country</option>
            <option value="India">India</option>
            <option value="USA">USA</option>
          </select>
          {formState.errors.country && <div className="error">{formState.errors.country}</div>}
        </div>
        <div>
          <select name="city" value={formState.city} onChange={handleChange}>
            <option value="">Select City</option>
            <option value="Delhi">Delhi</option>
            <option value="New York">New York</option>
          </select>
          {formState.errors.city && <div className="error">{formState.errors.city}</div>}
        </div>
        <div>
          <input
            type="text"
            name="panNo"
            placeholder="Pan No."
            value={formState.panNo}
            onChange={handleChange}
          />
          {formState.errors.panNo && <div className="error">{formState.errors.panNo}</div>}
        </div>
        <div>
          <input
            type="text"
            name="aadharNo"
            placeholder="Aadhar No."
            value={formState.aadharNo}
            onChange={handleChange}
          />
          {formState.errors.aadharNo && <div className="error">{formState.errors.aadharNo}</div>}
        </div>
        <button type="submit" disabled={Object.keys(formState.errors).length > 0}>
          Submit
        </button>
      </form>
    </div>
  );
};

export default FormComponent;
